package com.brothergeol.springDB.repository;import com.brothergeol.springDB.domain.Member;import javax.sql.DataSource;import lombok.extern.slf4j.Slf4j;import org.springframework.jdbc.core.JdbcTemplate;/** * SQLExceptionTranslator 추가 */@Slf4jpublic class MemberRepositoryV5 implements MemberRepository {    private final JdbcTemplate jdbcTemplate;    public MemberRepositoryV5(DataSource dataSource) {        this.jdbcTemplate = new JdbcTemplate(dataSource);    }    @Override    public Member save(Member member) {        String sql = "insert into member(member_id, money) values(?, ?)";        jdbcTemplate.update(sql, member.getMemberId(), member.getMoney());        return member;    }    @Override    public Member findById(String memberId) {        String sql = "select * from member where member_id = ?";        return jdbcTemplate.queryForObject(sql, (rs, rowNum) -> {            return new Member(rs.getString("member_id"), rs.getInt("money"));        }, memberId);    }    @Override    public void update(String memberId, int money) {        String sql = "update member set money=? where member_id=?";        jdbcTemplate.update(sql, money, memberId);    }    @Override    public void delete(String memberId) {        String sql = "delete from member where member_id=?";        jdbcTemplate.update(sql, memberId);    }}